INSERT INTO `conference_servise` (`id`, `room_name`, `room_url`, `img_thumbal`, `img`, `svg`, `description`, `order_block`, `NN`, `category`, `conference_bacground`) VALUES
(1, 'Сотрудничество', 'Cooperation2', 100, 100, 2, 'Аудитория «Сотрудничество» - большой и комфортный конференц-зал, оборудованный современной техникой и всеми возможными вариантами меблировки. Общая площадь зала – 180,6 кв. м. Вариативность расстановки мебели позволяет разместить даже большое количество участников наиболее удобно для проведения Вашего мероприятия.', '', 0, 3, 'rgba(122,184,0,1)'),
(2, 'Системность + Инновационность', 'system-innovation', 108, 108, 2, 'Объединенные Аудитории «Системность» и «Инновационность», общей площадью 101,58 кв. м, позволяет разместить около 80  участников.  В объединенной аудитории находятся 2 экрана, что позволяет всем участникам полноценно воспринимать информацию всем зрителям, независимо от их месторасположения в аудитории.', '', 0, 4, 'rgba(255,121,0,1)'),
(3, 'Результативность', 'rezultativnost', 107, 107, 2, '«Результативность» - аудитория для проведения тренингового формата обучения, работы в команде и проведения бизнес-игр. Общая площадь аудитории – 49,02 кв. м позволяет разместить около 30 участников, создавая учебную или рабочую атмосферу.', '', 0, 5, 'rgba(0,198,215,1)'),
(4, 'Коммуникативность', 'communicativnost', 99, 99, 2, 'Площадь аудитории «Коммуникативность» - 55,77 кв.м. Как и «Организация деятельности», аудитория оборудована современными мобильными рабочими местами-стульями, с удобной столешницей. Такое решение позволяет участникам не уставать во время обучения и делать заметки, а тренеру - быстро изменить расстановку в аудитории для смены деятельности. Аудитория прекрасно подходит для проведения неформальных клубов, тренингов и командообразующих мероприятий', '', 0, 5, 'rgba(240,171,0,1)'),
(5, 'Развитие', 'Razvitie2', 105, 105, 2, 'Аудитория «Развитие» - Ваша комфортная площадка для проведения конференций, обучающих программ и бизнес-игр. В зале площадью 79,41 кв. м. можно удобно работать в группах и проводить конференции информативного характера, совещания за большим \"круглым столом\" и тренинги с высокой активностью. Данная аудитория имеет выход на отдельную террасу, что позволяет проводить в ней VIP-мероприятия.', '', 0, 4, 'rgba(139,141,142,1)'),
(6, 'Системность', 'systemnost', 109, 109, 2, 'Аудитория «Системность», общей площадью 50,7кв. м, позволяет разместить около 35 участников. Уникальная особенность данной аудитории состоит в том, что ее можно объединить с аналогичным залом «Инновационность» в один большой конференц-зал (101,58 кв.м), в котором можно разместить до 100 участников. В объединенной аудитории находятся 2 экрана, что позволяет всем участникам полноценно воспринимать информацию всем участникам, независимо от их месторасположения в аудитории.', '', 0, 5, 'rgba(113, 92, 76,1)'),
(7, 'Инновационность', 'innovation', 102, 102, 2, 'Аудитория «Инновационность», общей площадью 50,88 кв. м, позволяет разместить около 35 участников. Как и «Системность», она обладает возможностью совмещения с аналогичным залом, создав аудиторию размером 101,58 кв.м, в которой можно разместить до 100 участников. В объединенной аудитории находятся 2 экрана, что позволяет всем участникам полноценно воспринимать информацию всем зрителям, независимо от их месторасположения в аудитории', '', 0, 5, 'rgba(122,184,0,1)'),
(8, 'Организация деятельности', 'organisation', 104, 104, 2, 'Аудитория, площадью 45,2 кв.м. Она оборудована современными мобильными рабочими местами-стульями, с удобной столешницей. Такое решение позволяет участникам не уставать во время обучения и делать заметки, а тренеру - быстро изменить расстановку в аудитории для смены деятельности. Аудитория прекрасно подходит для проведения неформальных клубов, тренингов и командообразующих мероприятий', '', 0, 5, 'rgba(113, 92, 76,1)'),
